function AssembleChartLinesGains(year) { $.ajax({ url: '/Dashboard/GraphicDataGains', method: 'GET', data: { year: year }, success: function (data) { new Chart(document.getElementById("GraphValuesGainsExpenses"), { type: 'line', data: { labels: GetMonths(data), datasets: [{ label: "Earnings From Payments Per Year", data: GetValues(data), backgroundColor: "#90caf9", borderColor: "#0277bd", fill: !0, lineTension: 0 }] }, options: { legend: { labels: { usePointStyle: !0 } }, scales: { xAxes: [{ gridLines: { display: !1 } }] } } }) } }) } function AssembleChartLinesExpenses(year) { $.ajax({ url: '/Dashboard/GraphicDataExpenses', method: 'GET', data: { year: year }, success: function (data) { debugger; new Chart(document.getElementById("GraphValuesGainsExpenses"), { type: 'line', data: { labels: GetMonths(data), datasets: [{ label: "Service Expenses Per Year", data: GetValues(data), backgroundColor: "#ffcdd2", borderColor: "#b71c1c", fill: !0, lineTension: 0 }] }, options: { legend: { labels: { usePointStyle: !0 } }, scales: { xAxes: [{ gridLines: { display: !1 } }] } } }) } }) } function AssembleChartExpensesTotalGains() { $.ajax({ url: '/Dashboard/GraphicDataExpensesTotalGains', method: 'GET', success: function (data) { new Chart(document.getElementById("GraphExpensesIncomesTotal"), { type: 'pie', data: { labels: ["Incomes", "Expenses"], datasets: [{ label: "Total Earnings and Expenses", data: [data.incomes, data.expenses], backgroundColor: ["#0091ea", "#c62828"] }] }, options: { legend: { labels: { usePointStyle: !0 } } } }) } }) }